package com.tnsif.Day1;

public class Char {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'l';
		System.out.println(ch);
		
		char ch1 = 76;
		System.out.println(ch1);
		
		char var1 = '\u221E';
		System.out.println(var1);
		
		char var2 = '\u2665';
		System.out.println(var2);
		
		int l = 'L';
		System.out.println(l);
	}

}
