		System.out.println("Min range of byte is" +byteMin+"Max range of byte is"+byteMax);
		
		short shortMax = 32767;
		short shortMin = -32768;
		System.out.println("Minshort of byte is" + shortMin+"Maxshort range of byte is"+shortMax);
		
		int maxInt = 2147483647;
		int minInt = -2147483648;
		System.out.println("Minint range of byte is" + shortMin+"Maxshort range of byte is "+shortMax);
		
		long maxLong = 974748789249284927L;
